def seed_database():
    """Seed the database with initial parts data."""
    # Create tables
    Base.metadata.create_all(bind=engine)

    db = SessionLocal()
    try:
        # Check if already seeded
        existing = db.query(Part).first()
        if existing:
            print("Database already seeded. Skipping...")
            return

        # Add parts
        for part_data in SEED_PARTS:
            part = Part(**part_data)
            db.add(part)

        db.commit()
        print(f"Successfully seeded {len(SEED_PARTS)} parts!")

        # Print summary
        promoters = sum(1 for p in SEED_PARTS if p["type"] == "promoter")
        rbs = sum(1 for p in SEED_PARTS if p["type"] == "rbs")
        terminators = sum(1 for p in SEED_PARTS if p["type"] == "terminator")
        genes = sum(1 for p in SEED_PARTS if p["type"] == "gene")

        print(f"  - {promoters} promoters")
        print(f"  - {rbs} RBS")
        print(f"  - {terminators} terminators")
        print(f"  - {genes} genes")

    finally:
        db.close()


if __name__ == "__main__":
    seed_database()
